% growth of population following a Logistic function a./(1+exp(-b*(x-c)))
pop=zeros(12,340); % 1971 to 2300
for z=2:12
    y=inputs(z,97:144);
    L1=y(end)*5;
    y3=log(L1./y - 1);
    t2=[1:48]; [sR,s1,s2] = regression(t2,y3); 
    L2=-s1;
    L3=s2/L2;
    model=fittype(@(a,b,c,x) a./(1+exp(-b*(x-c))));
    options=fitoptions('Method','NonlinearLeastSquares','StartPoint',[L1 L2 L3],'Lower',[y(end) 0 5],'Upper',[y(end)*2 1 50]);
    [f, gof]=fit(t2', y', model, options);
    for t=1:340
        pop(z,t)=f(t);
        pop(1,t)=pop(1,t)+pop(z,t);
    end
