inv2 = sum(econo0(1,(30+(z-1)*Ne):(36+(z-1)*Ne)),2);
%correction for new-built investments t$
econo0(1,(30+(z-1)*Ne):(36+(z-1)*Ne)) = econo0(1,(30+(z-1)*Ne):(36+(z-1)*Ne)) .*(inv0/inv2);
%correction for new-built power generation PJ/yr
econo0(1,(37+(z-1)*Ne):(43+(z-1)*Ne)) = econo0(1,(37+(z-1)*Ne):(43+(z-1)*Ne)) .*(inv0/inv2);
%CO2 emission factors for fossil fuel
efco20(z) = IE0/(e0-gpw0); % co2 emission factor
%fossil fuel retired PJ/yr
ffout=(e0-gpw0)/mean(lifetimeff,2);

%1 EUE; 2 EPE; 3 ENE; 4 saving rate;
%5 abatement cost as a percentage of GDP; 6 climate damage as a percentage of GDP; 7 net output
%8 fraction of labor allocation to energy; 9 fraction of investment allocation to energy
%10 capital (trill $); 11 energy capital (trill $); 12 energy (PJ); 13 output (t$); 14 energy price $/kWh; 15 omega; 16 discounted utility
%17 CO2 emissions Gt CO2; 18 carbon tax revenue t$; 19 climate investment t$; 20 new-built fossil fuel PJ/yr; 21 renewable energy PJ/yr; 22 remaining climate investment t$
%23-29 abated emission GtCO2/yr; 30-36 new-built investments t$; 37-43 new-built power generation PJ/yr; 44-50 low-carbon power generation PJ/yr
%51-61 investment transfer from region 1-11 t$; 62 OM costs t$/yr; 63 net transfer of money from other regions i t$/y
econo0(1,(1+(z-1)*Ne):(22+(z-1)*Ne)) = [eue0, epe0, ene0, sav0, abate0, D0, q2, Le0, Ve0, K0, Ke0, e0, q0, pe0, se0, util0, IE2, 0, inv0, ffout, gpw0, 0 ];
for j=1:Ne
    if j>6 && j~=8 && j~=9 && j~=14 && j~=15
        econo0(1,j)=econo0(1,j)+econo0(1,j+(z-1)*Ne); % sum
    else
        econo0(1,j)=econo0(1,j)+econo0(1,j+(z-1)*Ne)/11; % average
    end
end

end

% Coefficients in the relationships of the induced efficiency change
iec = calib_iec ( inputs, alpha, elas );

% Calibration of the induced efficiency change based on the future trend of energy and output in the IPCC scenarios
iec2=iec;
wgc=zeros(12,5);
for z=1:12
    tlen=7;
    x=[2020:5:(2015+5*tlen)]; % year
    y=iiasa(z,1:tlen)./pop(z,50:5:(45+5*tlen)); [sR,lr_e00,bb0] = regression(x,log(y)); % e=E/L
    y=iiasa(z,18:(17+tlen))./pop(z,50:5:(45+5*tlen)); [sR,lr_y00,bb00] = regression(x,log(y)); % y=Y/L
    lr_e0=lr_e00;
    lr_y0=lr_y00; 
    lr_pe0=0;
    lr_k0=lr_y0; % steady state
    se0=econo0(1,15+(z-1)*Ne);    
    lr_se0 = lr_pe0 + lr_e0 - lr_y0;
    lr_B = lr_e0 - alpha*lr_k0;
    lr_A = lr_y0 - alpha*lr_k0;
    lr_taue0 = elas/(elas-1)*lr_se0 - lr_e0 + lr_y0;
    lr_we0 = lr_B - lr_se0;
    lr_b0 = (lr_A - se0*(lr_we0 + lr_taue0))/(1-se0);
    zdev=iec(1+(z-1)*3,1)+iec(1+(z-1)*3,2)*log10(se0)-lr_taue0;
    iec2(1+(z-1)*3,1:2:5)=iec(1+(z-1)*3,1:2:5)-zdev;
    zdev=iec(2+(z-1)*3,1)+iec(2+(z-1)*3,2)*log10(se0)-lr_we0;
    iec2(2+(z-1)*3,1:2:5)=iec(2+(z-1)*3,1:2:5)-zdev;    
    zdev=iec(3+(z-1)*3,1)+iec(3+(z-1)*3,2)*log10(1-se0)-lr_b0;
    iec2(3+(z-1)*3,1:2:5)=iec(3+(z-1)*3,1:2:5)-zdev;
    wgc(z,1:5)=[lr_e0 lr_y0 lr_taue0 lr_we0 lr_b0];
    
    lr_e0=0.001;
    lr_y0=0.01;
    if z==11
        lr_y0=0.005;
    elseif z==12
        lr_y0=0.005;
    end
    lr_pe0=0;
    lr_k0=lr_y0; % steady state
    se0=econo0(1,15+(z-1)*Ne);    
    lr_se0 = lr_pe0 + lr_e0 - lr_y0;
    lr_B = lr_e0 - alpha*lr_k0;
    lr_A = lr_y0 - alpha*lr_k0;
    lr_taue0 = elas/(elas-1)*lr_se0 - lr_e0 + lr_y0;
    lr_we0 = lr_B - lr_se0;
    lr_b0 = (lr_A - se0*(lr_we0 + lr_taue0))/(1-se0);
    zdev=iec(1+(z-1)*3,1)+iec(1+(z-1)*3,2)*log10(se0)-lr_taue0;
    iec2(1+(z-1)*3,9)=iec(1+(z-1)*3,1)-zdev;
    zdev=iec(2+(z-1)*3,1)+iec(2+(z-1)*3,2)*log10(se0)-lr_we0;
    iec2(2+(z-1)*3,9)=iec(2+(z-1)*3,1)-zdev;    
    zdev=iec(3+(z-1)*3,1)+iec(3+(z-1)*3,2)*log10(1-se0)-lr_b0;
    iec2(3+(z-1)*3,9)=iec(3+(z-1)*3,1)-zdev;
end

iec2(:,9)=iec2(:,9)-iec2(:,1);

iec=iec2; clear iec2;



